import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useSocket } from '../../context/SocketContext';

const ChatBot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useAuth();
  const socket = useSocket();
  const messagesEndRef = useRef(null);

  // Scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Fetch quick replies on component mount
  useEffect(() => {
    if (isOpen) {
      fetchQuickReplies();
    }
  }, [isOpen]);

  const fetchQuickReplies = async () => {
    try {
      // In a real implementation, this would fetch from the backend
      // const response = await api.get('/chatbot/quick-replies');
      // setQuickReplies(response.data.data.quickReplies);

      // For demo purposes, using dummy data
      const dummyQuickReplies = [
        "What's my fee status?",
        "How is my attendance?",
        "What's my schedule today?",
        "I need help with my grades",
        "How do I pay fees?",
        "What assignments are due?"
      ];

      // Set up to handle quick replies
      setMessages(prev => [...prev, {
        id: Date.now(),
        text: "Hello! I'm your school assistant. How can I help you today?",
        sender: 'bot',
        timestamp: new Date(),
        quickReplies: dummyQuickReplies
      }]);
    } catch (error) {
      console.error('Error fetching quick replies:', error);
    }
  };

  // Handle sending message to chatbot
  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      text: inputMessage,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      // In a real implementation, this would call the backend API
      // const response = await api.post('/chatbot/chat', {
      //   message: inputMessage,
      //   sessionId: `chat_${user._id}_${Date.now()}`
      // });

      // For demo purposes, using a simple response logic
      await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API delay

      // Simple response logic based on keywords
      let botResponse;
      const lowerInput = inputMessage.toLowerCase();

      if (lowerInput.includes('fee') || lowerInput.includes('pay')) {
        botResponse = "You have 2 pending fees: Tuition fee of ₹15,000 due on 2023-09-30 and Library fee of ₹1,000 due on 2023-09-15. Would you like to process payment?";
      } else if (lowerInput.includes('attendance') || lowerInput.includes('present')) {
        botResponse = "Your attendance rate is 93% this month. You were present for 28 out of 30 days. Keep up the good work!";
      } else if (lowerInput.includes('grade') || lowerInput.includes('score') || lowerInput.includes('mark')) {
        botResponse = "Your recent grades: Mathematics - A (90%), Science - A (90%), English - B+ (85%). Your overall GPA is 3.7/4.0.";
      } else if (lowerInput.includes('schedule') || lowerInput.includes('time table')) {
        botResponse = "Your classes today: Mathematics (9:00-9:45 AM), Science (10:00-10:45 AM), English (11:00-11:45 AM).";
      } else if (lowerInput.includes('assignment') || lowerInput.includes('homework')) {
        botResponse = "You have 1 assignment due tomorrow: Mathematics Chapter 1 Homework. Max points: 50.";
      } else {
        botResponse = "I'm here to help! I can provide information about your fees, attendance, grades, schedule, and assignments. What would you like to know?";
      }

      const botMessage = {
        id: Date.now() + 1,
        text: botResponse,
        sender: 'bot',
        timestamp: new Date(),
        action: null // In a real implementation, this would come from the backend
      };

      setMessages(prev => [...prev, botMessage]);

    } catch (error) {
      const errorMessage = {
        id: Date.now() + 1,
        text: 'Sorry, I encountered an error. Please try again.',
        sender: 'bot',
        timestamp: new Date(),
        isError: true
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle quick reply click
  const handleQuickReply = (text) => {
    setInputMessage(text);
    setTimeout(() => sendMessage(), 300); // Allow state to update
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  if (!isOpen) {
    return (
      <button
        onClick={() => setIsOpen(true)}
        className="fixed bottom-6 right-6 bg-blue-600 text-white p-4 rounded-full shadow-lg hover:bg-blue-700 transition-colors z-50 flex items-center justify-center"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 10h.01M12 10h.01M16 10h.01M9 16H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-5l-5 5v-5z" />
        </svg>
        <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
          3
        </span>
      </button>
    );
  }

  return (
    <div className="fixed bottom-6 right-6 w-96 h-[500px] bg-white rounded-lg shadow-xl border border-gray-200 flex flex-col z-50">
      {/* Header */}
      <div className="bg-blue-600 text-white p-4 rounded-t-lg flex justify-between items-center">
        <div className="flex items-center">
          <div className="bg-green-400 rounded-full w-3 h-3 mr-2"></div>
          <span className="font-semibold">School Assistant</span>
        </div>
        <button
          onClick={() => setIsOpen(false)}
          className="text-white hover:text-gray-200"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
          </svg>
        </button>
      </div>

      {/* Messages Container */}
      <div className="flex-1 overflow-y-auto p-4 bg-gray-50">
        {/* Welcome message */}
        {messages.length === 0 && (
          <div className="mb-4">
            <div className="bg-white p-3 rounded-lg shadow-sm border border-gray-200">
              <p className="text-sm text-gray-700">
                Hello! I'm your school assistant. I can help with:
              </p>
              <ul className="mt-2 text-sm text-gray-600">
                <li>• Student profile information</li>
                <li>• Fee status and payments</li>
                <li>• Attendance records</li>
                <li>• Course schedules</li>
                <li>• Creating support tickets</li>
              </ul>
            </div>
          </div>
        )}

        {/* Messages */}
        {messages.map((message) => (
          <div
            key={message.id}
            className={`mb-4 flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                message.sender === 'user'
                  ? 'bg-blue-500 text-white'
                  : message.isError
                  ? 'bg-red-100 text-red-800'
                  : 'bg-white text-gray-800 border border-gray-200'
              }`}
            >
              <p className="text-sm">{message.text}</p>
              {message.timestamp && (
                <p className="text-xs opacity-70 mt-1">
                  {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </p>
              )}
              {message.quickReplies && (
                <div className="mt-2 flex flex-wrap gap-1">
                  {message.quickReplies.map((reply, index) => (
                    <button
                      key={index}
                      onClick={() => handleQuickReply(reply)}
                      className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded hover:bg-blue-200 transition-colors"
                    >
                      {reply}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>
        ))}

        {isLoading && (
          <div className="flex justify-start mb-4">
            <div className="bg-white p-3 rounded-lg shadow-sm border border-gray-200">
              <div className="flex space-x-2">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div className="p-4 border-t border-gray-200 bg-white">
        <div className="flex space-x-2">
          <textarea
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type your message..."
            className="flex-1 border border-gray-300 rounded-lg px-3 py-2 text-sm resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="2"
            disabled={isLoading}
          />
          <button
            onClick={sendMessage}
            disabled={isLoading || !inputMessage.trim()}
            className={`px-4 py-2 rounded-lg text-white ${
              isLoading || !inputMessage.trim()
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-blue-600 hover:bg-blue-700'
            }`}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
              <path d="M10.894 2.553a1 1 0 00-1.788 0l-7 14a1 1 0 001.169 1.409l5-1.429A1 1 0 009 15.571V11a1 1 0 112 0v4.571a1 1 0 00.725.962l5 1.428a1 1 0 001.17-1.408l-7-14z" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
