import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import gsap from "gsap";
import NavbarNew from "../components/common/NavbarNew";

const HomeNew = () => {
  const { user } = useAuth();
  const [currentSlide, setCurrentSlide] = useState(0);
  const heroRef = useRef(null);
  const featuresRef = useRef(null);
  const statsRef = useRef(null);
  const coursesRef = useRef(null);

  const slides = [
    {
      image: "/banner1.jpeg",
      title: "Welcome to Excellence in Education",
      subtitle: "Empowering minds, shaping futures",
    },
    {
      image: "/banner.jpeg",
      title: "Learn From Expert Faculty",
      subtitle: "World-class education at your fingertips",
    },
    {
      image: "/banner3.jpeg",
      title: "Build Your Future Today",
      subtitle: "Join thousands of successful students",
    },
    {
      image: "/banner5.jpeg",
      title: "Innovation Meets Education",
      subtitle: "Modern learning for the modern world",
    },
  ];

  useEffect(() => {
    // Auto-rotate carousel
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [slides.length]);

  useEffect(() => {
    // Hero section animation
    if (heroRef.current) {
      gsap.from(heroRef.current.children, {
        opacity: 0,
        y: 50,
        duration: 1,
        stagger: 0.2,
        ease: "power3.out",
      });
    }

    // Features animation on scroll
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            gsap.from(entry.target.children, {
              opacity: 0,
              y: 30,
              duration: 0.8,
              stagger: 0.15,
              ease: "power2.out",
            });
          }
        });
      },
      { threshold: 0.1 }
    );

    if (featuresRef.current) observer.observe(featuresRef.current);
    if (statsRef.current) observer.observe(statsRef.current);
    if (coursesRef.current) observer.observe(coursesRef.current);

    return () => observer.disconnect();
  }, []);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const courses = [
    {
      name: "Islamiat",
      description: "Islamic Studies and Religious Education",
      icon: "ðŸ•Œ",
